DROP TABLE IF EXISTS `tmp_ajuste_perfil`;
CREATE TABLE `tmp_ajuste_perfil` (
  `Tipo` varchar(255) DEFAULT NULL,
  `codusuario` varchar(255) DEFAULT NULL,
  `codsituacao` varchar(255) DEFAULT NULL,
  `Matricula` varchar(255) DEFAULT NULL,
  `nome` varchar(255) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL,
  `hierarquia` varchar(255) DEFAULT NULL,
  `codsecao` varchar(255) DEFAULT NULL,
  `Unidade` varchar(255) DEFAULT NULL,
  `Perfil` varchar(255) DEFAULT NULL,
  `Letra` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

