-- Ajuste de Perfil no GRC
UPDATE plu_usuario u
INNER JOIN tmp_ajuste_perfil t ON t.codusuario = u.login
INNER JOIN plu_perfil p ON t.Perfil = p.nm_perfil
SET u.ativo = 'S',
 u.ativo_pir = 'S',
 u.tipo_usuario = 'P',
 u.ldap = 'S',
 u.id_perfil = p.id_perfil;
 
 -- PA
UPDATE plu_usuario u
INNER JOIN tmp_ajuste_perfil t ON t.codusuario = u.login
INNER JOIN db_pir.sca_organizacao_secao pa ON t.codsecao = pa.codigo
SET u.idt_unidade_regional = pa.idt
where pa.posto_atendimento = 'S';

UPDATE plu_usuario u
INNER JOIN tmp_ajuste_perfil t ON t.codusuario = u.login
INNER JOIN db_pir.sca_organizacao_secao pa ON t.codsecao = pa.codigo
SET u.idt_unidade_regional = null
where pa.posto_atendimento = 'N';

-- Relacao PA
SELECT
u.id_usuario as idt_usuario, pa.idt as idt_ponto_atendimento, 'A' as relacao, t.Letra as letra_painel
FROM
tmp_ajuste_perfil t
INNER JOIN plu_usuario u ON t.codusuario = u.login
INNER JOIN db_pir.sca_organizacao_secao pa ON t.codsecao = pa.codigo
LEFT OUTER JOIN grc_atendimento_pa_pessoa rpa ON pa.idt = rpa.idt_ponto_atendimento and u.id_usuario = rpa.idt_usuario
where pa.posto_atendimento = 'S'
and rpa.idt is null;

-- Usuário PIR
UPDATE db_pir.plu_usuario u
INNER JOIN tmp_ajuste_perfil t ON t.codusuario = u.login
SET u.ativo = 'S',
 u.ativo_pir = 'S',
 u.tipo_usuario = 'P';

-- Usuário PIR x GRC
SELECT
u.id_usuario as idt_usuario, 92 as idt_empreendimento
FROM
tmp_ajuste_perfil t
INNER JOIN db_pir.plu_usuario u ON t.codusuario = u.login
LEFT OUTER JOIN db_pir.plu_usuario_empreendimento ue ON u.id_usuario = ue.id_usuario and ue.idt_empreendimento = 92
where ue.idt_usuario_empreendimento is null;

