INSERT INTO `db_pir_grc`.`grc_parametros` (`codigo`, `descricao`, `ativo`, `html`, `detalhe`, `classificacao`) VALUES ('evento_declaracao_modelo_cont', 'Modelo da Declaração na Prestação de Contas do Evento', 'S', 'S', '<p><style type=\"text/css\">\r\n    div.tit {\r\n        text-align: center;\r\n        font-size: 18px;\r\n        font-weight: bold;\r\n        padding-top: 60px;\r\n    }\r\n    \r\n    div.txt1 {\r\n        font-size: 14px;\r\n        padding-top: 80px;\r\n    }\r\n    \r\n    div.txt2 {\r\n        text-align: right;\r\n        font-size: 14px;\r\n        padding-top: 80px;\r\n        font-weight: bold;\r\n    }\r\n</style></p>\r\n<div class=\"tit\">DECLARA&Ccedil;&Atilde;O DE CONCLUS&Atilde;O DOS SERVI&Ccedil;OS</div>\r\n<div class=\"txt1\">Eu, #nome_gerente#, matr&iacute;cula n&ordm; #matricula#, no uso das atribui&ccedil;&otilde;es  do cargo de #cargo#, declaro para os devidos fins, que o(s)  material(is) e/ou servi&ccedil;o(s) correspondente(s) ao evento N&ordm; #codigo_evento#,  Ordem(ns) de Servi&ccedil;o(s) N&ordm; #codigo_os#, foi(ram) recebido(s) e/ou  prestado(s), liberando-se o processo para pagamento.</div>\r\n<div class=\"txt2\">#cidade#, #estado#, #dt_aprovacao#.</div>', '02.06');
