INSERT INTO `db_pir_grc`.`grc_parametros` (`codigo`, `descricao`, `ativo`, `html`, `detalhe`, `classificacao`) VALUES ('evento_declaracao_modelo_rod', 'Modelo da Declaração na Prestação de Contas do Evento (Rodape)', 'S', 'S', '<p><style type=\"text/css\">\r\n    div.footer_txt1 {\r\n        font-size: 12px;\r\n        font-style: italic;\r\n    }\r\n\r\n    div.footer_txt2 {\r\n        text-align: right;\r\n        font-size: 12px;\r\n        font-style: italic;\r\n        padding-top: 20px;\r\n        padding-bottom: 20px;\r\n    }\r\n</style></p>\r\n<div class=\"footer_txt1\">Este &eacute; um documento gerado eletronicamente pelo Sistema de Gest&atilde;o de Finan&ccedil;as GFI, ap&oacute;s a  <b>autoriza&ccedil;&atilde;o de pagamento</b>, realizada pelo Gerente/Adjunto da Unidade Regional respons&aacute;vel pela realiza&ccedil;&atilde;o do evento.</div>\r\n<div class=\"footer_txt2\">C&oacute;digo de Autenticidade: #md5#</div>\r\n<div style=\"text-align: center;\"><img src=\"http://pir.ba.sebrae.com.br/sebrae_grc/admin/imagens/rel_rodape.png\" alt=\"\" /></div>', '02.08');

