INSERT INTO `db_pir_grc`.`grc_agenda_emailsms` (`idt`, `codigo`, `descricao`, `ativo`, `detalhe`, `idt_processo`, `tipo`) VALUES ('1', '01.01', 'COMPROVANTE DE AGENDAMENTO', 'S', '<p class=\"MsoNormal\" style=\"text-align:justify\">Ol&aacute; &nbsp;#cliente_pf!</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Seu atendimento foi agendado com sucesso. Nosso orientador de neg&oacute;cios estar&aacute; te aguardando no SEBRAE, #endereco_pa, na &nbsp;#dia_co_semana &nbsp;#data &agrave;s #hora, hor&aacute;rio local.</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">#ponto_atendimento</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">#unidade_regional</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">#endereco_pa</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Lembre-se de chegar com #tolerancia_atraso minutos de anteced&ecirc;ncia para garantir o atendimento na hora agendada.</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Caso necessite agendar um novo hor&aacute;rio ou realizar o cancelamento, voc&ecirc; poder&aacute; faz&ecirc;-lo em at&eacute; 48h antes do hor&aacute;rio marcado atrav&eacute;s &nbsp;da Central de Relacionamento SEBRAE pelo n&uacute;mero 0800 570 0800.</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\"><img src=\"/sebrae_grc/admin/fckupload/images/logo_sebrae.jpg\" width=\"236\" height=\"76\" alt=\"\" /></p>\r\n<p><span style=\"font-size:12.0pt;mso-bidi-font-size:11.0pt;line-height:115%;&#10;font-family:&quot;Times New Roman&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;;&#10;mso-ansi-language:PT-BR;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">Mensagem autom&aacute;tica. Favor n&atilde;o responder</span></p>', '3', 'E');
INSERT INTO `db_pir_grc`.`grc_agenda_emailsms` (`idt`, `codigo`, `descricao`, `ativo`, `detalhe`, `idt_processo`, `tipo`) VALUES ('2', '01.02', 'COMPROVANTE DE REAGENDAMENTO', 'S', '<p>&nbsp;<span style=\"text-align: justify;\">Ol&aacute; #NomeCliente!</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Seu atendimento foi reagendado de #DataAnterior &agrave;s #HorarioAnterior para #DiaSemana dia #data &agrave;s #Horario, hor&aacute;rio local. Nosso orientador de neg&oacute;cios estar&aacute; te aguardando no SEBRAE, &nbsp;#Endereco.</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Lembre-se de chegar com 10 minutos de anteced&ecirc;ncia para garantir o atendimento na hora agendada.</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Caso necessite agendar um novo hor&aacute;rio ou realizar o cancelamento, voc&ecirc; poder&aacute; faz&ecirc;-lo em at&eacute; 48h antes do hor&aacute;rio marcado atrav&eacute;s dos Totens dispon&iacute;veis nos pontos de atendimento, da Central de Relacionamento SEBRAE pelo n&uacute;mero 0800 570 0800 e do Portal de Atendimento pelo endere&ccedil;o: <a href=\"https://meuatendimento.sebrae.com.br/sites/PortalSebrae/login\">https://meuatendimento.sebrae.com.br/sites/PortalSebrae/login</a>.</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\"><img src=\"/sebrae_grc/admin/fckupload/images/logo_sebrae.jpg\" width=\"236\" height=\"76\" alt=\"\" /></p>\r\n<p class=\"MsoNormal\">Mensagem autom&aacute;tica. Favor n&atilde;o responder.</p>', '4', 'E');
INSERT INTO `db_pir_grc`.`grc_agenda_emailsms` (`idt`, `codigo`, `descricao`, `ativo`, `detalhe`, `idt_processo`, `tipo`) VALUES ('3', '03.01', 'NÃO COMPARECIMENTO DE CLIENTE', 'S', '<p>&nbsp;Ol&aacute; #NomeCliente!</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Seu atendimento foi agendado com sucesso. Nosso orientador de neg&oacute;cios estar&aacute; te aguardando no SEBRAE, #Endereco, na #DiaSemana #Data &agrave;s #Horario, hor&aacute;rio local.</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Lembre-se de chegar com 10 minutos de anteced&ecirc;ncia para garantir o atendimento na hora agendada.</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Notamos que o senhor(a) n&atilde;o compareceu ao nosso ponto de atendimento no dia #DataAnterior nem realizou o cancelamento no prazo estabelecido. Ressaltamos que, em casos de reincid&ecirc;ncia, haver&aacute; suspens&atilde;o do agendamento de um novo atendimento por um per&iacute;odo de <span style=\"color:red\">7</span> <span style=\"color:red\">dias.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify\">Caso necessite agendar um novo hor&aacute;rio ou realizar o cancelamento, voc&ecirc; poder&aacute; faz&ecirc;-lo em at&eacute; 48h antes do hor&aacute;rio marcado atrav&eacute;s dos Totens dispon&iacute;veis nos pontos de atendimento, da Central de Relacionamento SEBRAE pelo n&uacute;mero 0800 570 0800 e do Portal de Atendimento pelo endere&ccedil;o: <a href=\"https://meuatendimento.sebrae.com.br/sites/PortalSebrae/login\">https://meuatendimento.sebrae.com.br/sites/PortalSebrae/login</a>.</p>\r\n<p class=\"MsoNormal\"><img src=\"/sebrae_grc/admin/fckupload/images/logo_sebrae.jpg\" width=\"236\" height=\"76\" alt=\"\" /></p>\r\n<p class=\"MsoNormal\">Mensagem autom&aacute;tica. Favor n&atilde;o responder.</p>', '7', 'E');
INSERT INTO `db_pir_grc`.`grc_agenda_emailsms` (`idt`, `codigo`, `descricao`, `ativo`, `detalhe`, `idt_processo`, `tipo`) VALUES ('4', '02.02', 'Solicitar informações de Presença', 'S', '<p class=\"MsoNormal\" style=\"text-align: left; margin-left: 40px;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-align: left;\">S&oacute; para testar</p>\r\n<p class=\"MsoNormal\" style=\"text-align: left;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-align: left;\">Ol&aacute;<span style=\"text-align: justify;\">Ol&aacute; &nbsp;#cliente_pf!</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">Seu atendimento foi agendado com sucesso. Nosso orientador de neg&oacute;cios estar&aacute; te aguardando no SEBRAE, #endereco_pa, na &nbsp;#dia_co_semana &nbsp;#data &agrave;s #hora, hor&aacute;rio local.</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">#ponto_atendimento</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">#unidade_regional</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">#endereco_pa</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">Lembre-se de chegar com #tolerancia_atraso minutos de anteced&ecirc;ncia para garantir o atendimento na hora agendada.</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">Caso necessite agendar um novo hor&aacute;rio ou realizar o cancelamento, voc&ecirc; poder&aacute; faz&ecirc;-lo em at&eacute; 48h antes do hor&aacute;rio marcado atrav&eacute;s &nbsp;da Central de Relacionamento SEBRAE pelo n&uacute;mero 0800 570 0800.</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><img src=\"http://desenvolvepir.ba.sebrae.com.br/sebrae_grc/admin/fckupload/images/logo_sebrae.jpg\" width=\"236\" height=\"76\" alt=\"\" /></p>\r\n<p><span style=\"font-size: 12pt; line-height: 18.4px; font-family: &quot;Times New Roman&quot;;\">Mensagem autom&aacute;tica. Favor n&atilde;o respond</span></p>', '2', 'E');
